package ch02;

public class CharacterEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1 = 'A';
		System.out.println(ch1); // 문자 출력
		System.out.println((int)ch1); // 문자로 숫자 출력
		
		char ch2 = 66;
		System.out.println(ch2); // 숫자를 문자로 출력
		
		int ch3 = 67;
		System.out.println(ch3); // 숫자 출력
		System.out.println((char)ch3); // 숫자를 문자로 출
	}

}
