mapper.insert:
insert into 
	spring5_board(
		boardnum
		, memberid
		, title
		, contents

		, originalfile
		, savedfile

	)
values(
	spring5_board_seq.nextval, 
		#{memberid}
		, #{title}
		, #{contents}

		, #{originalfile}
		, #{savedfile}

	)

mapper.update:
update spring5_board
title = #{title}, contents = #{contents},
where boardnum = #{boardnum}
